import React, { useState, useEffect } from "react";
import "./lista.css";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { TextField } from "@mui/material";

const theme = createTheme({
  status: {
    danger: "#e53e3e",
  },
  palette: {
    primary: {
      main: "#b9114b",
      darker: "#053e85",
    },
    neutral: {
      main: "#64748B",
      contrastText: "#fff",
    },
  },
});

const listaHabilidades = [
  "React",
  "Ract Native",
  "Vue",
  "Java",
  "Angular",
  "Nenhuma habilidade",
];

const listaPreferencias = ["Front-end", "Back-end"];

function Lista() {
  const [funcionario, setFuncionario] = useState({
    nomeFuncionario: "",
    funcao: "",
    habilidades: [],
    preferencias: "",
  });

  const [funcionarios, setFuncionarios] = useState([]);

  return (
    <div className="listaDeFuncionarios">
      <ThemeProvider theme={theme}>
        <h1>Ol√°</h1>
        Nome:
        <TextField
          value={funcionario.nomeFuncionario}
          onChange={(e) =>
            setFuncionario({ ...funcionario, nomeFuncionario: e.target.value })
          }
        ></TextField>
        Funcao: <TextField value={funcionario.funcao}></TextField>
        Habilidades: <TextField value={funcionario.habilidades}></TextField>
        Preferencias: <TextField value={funcionario.preferencias}></TextField>
      </ThemeProvider>
    </div>
  );
}

export default Lista;
