import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import ReactPaginate from "react-paginate";

function BasicTable({ funcionarios }) {
  const [pageNumber, setPageNumber] = useState(0);
  const rowsPerPage = 5;

  const pagesVisited = pageNumber * rowsPerPage;
  const pageCount = Math.ceil(funcionarios.length / rowsPerPage);

  const displayFuncionarios = funcionarios
    .slice(pagesVisited, pagesVisited + rowsPerPage)
    .map((funcionario, index) => (
      <TableRow
        key={index}
        sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
      >
        <TableCell component="th" scope="row">
          {funcionario.nome}
        </TableCell>
        <TableCell align="right">{funcionario.funcao}</TableCell>
        <TableCell align="right">{funcionario.habilidades}</TableCell>
        <TableCell align="right">{funcionario.preferencias}</TableCell>
      </TableRow>
    ));

  const handlePageClick = ({ selected }) => {
    setPageNumber(selected);
  };

  return (
    <div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Nome</TableCell>
              <TableCell align="right">Função</TableCell>
              <TableCell align="right">Habilidadaes</TableCell>
              <TableCell align="right">Prefências</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{displayFuncionarios}</TableBody>
        </Table>
      </TableContainer>
      <ReactPaginate
        previousLabel={"Anterior"}
        nextLabel={"Próxima"}
        pageCount={pageCount}
        onPageChange={handlePageClick}
        containerClassName={"pagination"}
        previousLinkClassName={"pagination__link"}
        nextLinkClassName={"pagination__link"}
        disabledClassName={"pagination__link--disabled"}
        activeClassName={"pagination__link--active"}
      />
    </div>
  );
}

export default BasicTable;
