import { IconButton, TextField } from "@mui/material";
import React, { useState } from "react";
import "./index.css";
import VisibilityIcon from "@mui/icons-material/Visibility";

function Login() {
  const [showPassword, setShowPassword] = useState(false);

  function mostrarPasswordClick() {
    setShowPassword((show) => !show);
  }

  return (
    <div className="telaLogin">
      <span style={{ marginRight: "180px" }}>E-mail</span> <br />
      <TextField type={"text"}></TextField> <br />
      <span style={{ marginRight: "180px" }}>Senha</span> <br />
      <TextField type={showPassword}>
        <IconButton type={showPassword ? "text" : "password"}></IconButton>
      </TextField>
    </div>
  );
}

export default Login;
