import {
  Dialog,
  DialogActions,
  DialogTitle,
  FormControl,
  TextField,
} from "@mui/material";

function DialogoAdicionarEditarInput() {
  return (
    <Dialog
      open={openAdicionar}
      TransitionComponent={TransitionAdicionar}
      keepMounted
      onClose={fecharAdicionar}
      aria-labelledby="alert-dialog-title"
      aria-describedby="alert-dialog-description"
      maxWidth="xl"
      PaperProps={{
        style: {
          maxHeight: "80vh",
          minHeight: "60vh",
          minWidth: "50vh",
          height: "25%",
        },
      }}
    >
      <DialogTitle id="alert-dialog-title">
        <span
          style={{ display: "flex", justifyContent: "center" }}
        >{`Digite os dados do funcionário: `}</span>
      </DialogTitle>
      <DialogActions
        style={{
          display: "flex",
          flexDirection: "column",
        }}
      >
        {/* div inputs  */}

        <div
          style={{
            marginTop: 20,
            display: "flex",
            flexDirection: "column",
          }}
        >
          {/* input nome */}

          <TextField
            id="outlined-basic"
            type="text"
            label="Nome"
            variant="outlined"
            size="large"
            value={inputNome}
            margin="dense"
            onChange={(e) => setInputNome(e.target.value)}
            style={{ marginRight: 15, marginLeft: 10, width: 300 }}
          ></TextField>

          {/* input função/select */}

          <TextField
            id="outlined-basic"
            type="text"
            label="Função"
            variant="outlined"
            size="large"
            value={inputFuncao}
            margin="dense"
            onChange={(e) => setInputFuncao(e.target.value)}
            style={{ marginRight: 15, marginLeft: 10, width: 300 }}
          ></TextField>

          {/* input/formulário/select habilidades */}

          <FormControl sx={{ width: 300 }}>
            <InputLabel
              id="demo-multiple-checkbox-label"
              style={{ marginLeft: 10, marginTop: 10 }}
            >
              Habilidades
            </InputLabel>
            <Select
              labelId="demo-multiple-checkbox-label"
              type="text"
              id="demo-multiple-checkbox"
              variant="outlined"
              label="Habilidades"
              multiple
              value={inputHabilidades}
              onChange={(e) => setInputHabilidades(e.target.value)}
              input={<OutlinedInput label="Habilidades" />}
              renderValue={(selected) => selected.join(", ")}
              MenuProps={MenuProps}
              style={{
                marginLeft: 10,
                marginRight: 15,
                marginTop: 9,
                width: 300,
              }}
            >
              {listaHabilidades.map((name, index) => (
                <MenuItem key={index} value={name}>
                  <Checkbox checked={inputHabilidades.indexOf(name) > -1} />
                  <ListItemText primary={name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          {/* input preferencias */}

          <FormControl sx={{ width: 300 }}>
            <InputLabel
              id="demo-simple-select-label"
              style={{ marginLeft: 10, marginTop: 12 }}
            >
              Preferências
            </InputLabel>
            <Select
              id="demo-simple-select"
              labelId="demo-simple-select-label"
              type="text"
              label="Preferências"
              variant="outlined"
              value={inputPreferencias}
              margin="dense"
              onChange={(e) => setInputPreferencias(e.target.value)}
              style={{
                marginTop: 12,
                width: 300,
                marginLeft: 10,
              }}
            >
              {listaPreferencias.map((preferencia, index) => (
                <MenuItem key={index} value={preferencia}>
                  {preferencia}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>

        <br />
        <br />
        <br />

        {/* Botão confirmação com operador ternário (true para adicionar e false para editar*/}

        {botaoEditarAdicionar ? (
          <Button variant="contained" onClick={adicionarSim}>
            Adicionar
          </Button>
        ) : (
          <Button variant="contained" onClick={editarSim}>
            Salvar
          </Button>
        )}

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            marginTop: 40,
          }}
        ></div>
      </DialogActions>
    </Dialog>
  );
}

export default DialogoAdicionarEditarInput;
